package org.example;public class BankAccount {    private String firstName;    private String lastName;    private double balance;    public BankAccount(String firstName, String lastName, double balance) {        this.firstName = firstName;        this.lastName = lastName;        this.balance = balance;    }    public String getFirstName() {        return firstName;    }    public void setFirstName(String firstName) {        this.firstName = firstName;    }    public String getLastName() {        return lastName;    }    public void setLastName(String lastName) {        this.lastName = lastName;    }    public double getBalance() {        return balance;    }    public void setBalance(double balance) {        this.balance = balance;    }    public double deposit(double amount){        return balance += amount;    }    public double withdraw(double amount){        if(balance - amount < 0){            throw new IllegalArgumentException("You don't have enough money to make that withdrawal!");        } else{            return balance -= amount;        }    }    @Override    public String toString() {        return "BankAccount{" + "firstName='" + firstName + '\'' + ", lastName='" + lastName + '\'' + ", balance=" + balance + '}';    }}